package syed;

public class loopsconcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
/*while loop: Disadvantage of whileloop is it will generate infinate loops if 
		we don't give increment or decremental part */
		
		
	int i=1;        // initilization
	while (i<=10) {  //conditional
		System.out.println(i);
     i=	i+1 ;         // incremental or decremental
     }
	
	System.out.println("......");	
// for loop; 	
	
	for (int j=1; j<=10; j++) {
		System.out.println(j);
	}
	
	}

}
